/**
 *
 * @param {ExecutionContext} context
 * @param {Object} remoteObject
 * @return {JSHandle|ElementHandle}
 */
function createJSHandle (context, remoteObject) {
  const frame = context.frame()
  if (remoteObject.subtype === 'node' && frame) {
    const frameManager = frame.frameManager()
    return new ElementHandle(
      context,
      context._client,
      remoteObject,
      frameManager.page(),
      frameManager
    )
  }
  return new JSHandle(context, context._client, remoteObject)
}

exports.createJSHandle = createJSHandle

class JSHandle {
  /**
   * @param {!ExecutionContext} context
   * @param {Chrome|CRIConnection|CDPSession|Object} client
   * @param {!Object} remoteObject
   */
  constructor (context, client, remoteObject) {
    this._context = context
    this._client = client
    this._remoteObject = remoteObject
    this._disposed = false
  }

  /**
   * @return {!ExecutionContext}
   */
  executionContext () {
    return this._context
  }

  /**
   * @param {string} propertyName
   * @return {Promise<JSHandle>}
   */
  async getProperty (propertyName) {
    const objectHandle = await this._context.evaluateHandle(
      (object, propertyName) => {
        const result = { __proto__: null }
        result[propertyName] = object[propertyName]
        return result
      },
      this,
      propertyName
    )
    const properties = await objectHandle.getProperties()
    const result = properties.get(propertyName) || null
    await objectHandle.dispose()
    return result
  }

  /**
   * @return {Promise<Map<string, !JSHandle>>}
   */
  async getProperties () {
    const response = await this._client.send('Runtime.getProperties', {
      objectId: this._remoteObject.objectId,
      ownProperties: true
    })
    const properties = new Map()
    const result = response.result
    for (let i = 0; i < result.length; i++) {
      const property = result[i]
      if (!property.enumerable) continue
      properties.set(
        property.name,
        createJSHandle(this._context, property.value)
      )
    }
    return properties
  }

  /**
   * @return {Promise<Object>}
   */
  async jsonValue () {
    if (this._remoteObject.objectId) {
      const response = await this._client.send('Runtime.callFunctionOn', {
        functionDeclaration: 'function() { return this; }',
        objectId: this._remoteObject.objectId,
        returnByValue: true,
        awaitPromise: true
      })
      return helper.valueFromRemoteObject(response.result)
    }
    return helper.valueFromRemoteObject(this._remoteObject)
  }

  /**
   * @return {?ElementHandle}
   */
  asElement () {
    return null
  }

  async dispose () {
    if (this._disposed) return
    this._disposed = true
    await helper.releaseObject(this._client, this._remoteObject)
  }

  /**
   * @override
   * @return {string}
   */
  toString () {
    if (this._remoteObject.objectId) {
      const type = this._remoteObject.subtype || this._remoteObject.type
      return `${this.constructor.name}@${type}`
    }
    return `${this.constructor.name}:${helper.valueFromRemoteObject(
      this._remoteObject
    )}`
  }

  /** @ignore */
  // eslint-disable-next-line space-before-function-paren
  [util.inspect.custom](depth, options) {
    return this.toString()
  }
}

exports.JSHandle = JSHandle

class ElementHandle extends JSHandle {
  /**
   * @param {!ExecutionContext} context
   * @param {Chrome|CRIConnection|CDPSession|Object} client
   * @param {!Object} remoteObject
   * @param {?Page} page
   * @param {?FrameManager} frameManager
   */
  constructor (context, client, remoteObject, page, frameManager) {
    super(context, client, remoteObject)
    /**
     * @type {Chrome|CRIConnection|CDPSession|Object}
     * @private
     */
    this._client = client
    /**
     * @type {!Object}
     * @private
     */
    this._remoteObject = remoteObject
    /**
     * @type {?Page}
     * @private
     */
    this._page = page
    /**
     * @type {?FrameManager}
     * @private
     */
    this._frameManager = frameManager
    this._disposed = false
  }

  /**
   * @override
   * @return {?ElementHandle}
   */
  asElement () {
    return this
  }

  /**
   * @returns {Promise<boolean>}
   */
  isIntersectingViewport () {
    return this.executionContext().evaluate(async element => {
      const visibleRatio = await new Promise(resolve => {
        const observer = new IntersectionObserver(entries => {
          resolve(entries[0].intersectionRatio)
          observer.disconnect()
        })
        observer.observe(element)
      })
      return visibleRatio > 0
    }, this)
  }

  /**
   * @param {string} selector
   * @return {Promise<ElementHandle|undefined>}
   */
  querySelector (selector) {
    return this.$(selector)
  }

  /**
   * @param {string} selector
   * @return {Promise<Array<ElementHandle>>}
   */
  querySelectorAll (selector) {
    return this.$$(selector)
  }

  /**
   * @param {string} selector
   * @param {Function|String} pageFunction
   * @param {...*} args
   * @return {Promise<Object|undefined>}
   */
  querySelectorEval (selector, pageFunction, ...args) {
    return this.$eval(selector, pageFunction, ...args)
  }

  /**
   * @param {string} selector
   * @param {Function|String} pageFunction
   * @param {...*} args
   * @return {Promise<Object|undefined>}
   */
  querySelectorAllEval (selector, pageFunction, ...args) {
    return this.$$eval(selector, pageFunction, ...args)
  }

  /**
   * @param {string} expression
   * @return {Promise<Array<ElementHandle>>}
   */
  xpathQuery (expression) {
    return this.$x(expression)
  }

  /**
   * @return {Promise<?Frame>}
   */
  async contentFrame () {
    const nodeInfo = await this._client.send('DOM.describeNode', {
      objectId: this._remoteObject.objectId
    })
    if (typeof nodeInfo.node.frameId !== 'string') return null
    return this._frameManager.frame(nodeInfo.node.frameId)
  }

  async hover () {
    await this.scrollIntoViewIfNeeded()
    const { x, y } = await this._clickablePoint()
    await this._page.mouse.move(x, y)
  }

  /**
   * @param {!{delay?: number, button?: "left"|"right"|"middle", clickCount?: number}=} options
   */
  async click (options) {
    await this.scrollIntoViewIfNeeded()
    const { x, y } = await this._clickablePoint()
    await this._page.mouse.click(x, y, options)
  }

  /**
   * @param {...string} filePaths
   */
  async uploadFile (...filePaths) {
    const files = filePaths.map(filePath => Path.resolve(filePath))
    const objectId = this._remoteObject.objectId
    await this._client.send('DOM.setFileInputFiles', { objectId, files })
  }

  async tap () {
    await this.scrollIntoViewIfNeeded()
    const { x, y } = await this._clickablePoint()
    await this._page.touchscreen.tap(x, y)
  }

  async focus () {
    await this.executionContext().evaluate(element => element.focus(), this)
  }

  /**
   * @param {string} text
   * @param {{delay: (number|undefined)}=} options
   */
  async type (text, options) {
    await this.focus()
    await this._page.keyboard.type(text, options)
  }

  /**
   * @param {string} key
   * @param {!{delay?: number, text?: string}=} options
   */
  async press (key, options) {
    await this.focus()
    await this._page.keyboard.press(key, options)
  }

  /**
   * @return {Promise<?{x: number, y: number, width: number, height: number}>}
   */
  async boundingBox () {
    const result = await this._getBoxModel()

    if (!result) return null

    const quad = result.model.border
    const x = Math.min(quad[0], quad[2], quad[4], quad[6])
    const y = Math.min(quad[1], quad[3], quad[5], quad[7])
    const width = Math.max(quad[0], quad[2], quad[4], quad[6]) - x
    const height = Math.max(quad[1], quad[3], quad[5], quad[7]) - y

    return { x, y, width, height }
  }

  /**
   * @return {Promise<?BoxModel>}
   */
  async boxModel () {
    const result = await this._getBoxModel()

    if (!result) return null

    const { content, padding, border, margin, width, height } = result.model
    return {
      content: this._fromProtocolQuad(content),
      padding: this._fromProtocolQuad(padding),
      border: this._fromProtocolQuad(border),
      margin: this._fromProtocolQuad(margin),
      width,
      height
    }
  }

  /**
   *
   * @param {!Object=} options
   * @returns {Promise<string|!Buffer>}
   */
  async screenshot (options = {}) {
    let needsViewportReset = false

    let boundingBox = await this.boundingBox()
    assert(boundingBox, 'Node is either not visible or not an HTMLElement')

    const viewport = this._page.viewport()

    if (
      viewport &&
      (boundingBox.width > viewport.width ||
        boundingBox.height > viewport.height)
    ) {
      const newViewport = {
        width: Math.max(viewport.width, Math.ceil(boundingBox.width)),
        height: Math.max(viewport.height, Math.ceil(boundingBox.height))
      }
      await this._page.setViewport(Object.assign({}, viewport, newViewport))

      needsViewportReset = true
    }

    await this.scrollIntoViewIfNeeded()

    boundingBox = await this.boundingBox()
    assert(boundingBox, 'Node is either not visible or not an HTMLElement')
    assert(boundingBox.width !== 0, 'Node has 0 width.')
    assert(boundingBox.height !== 0, 'Node has 0 height.')

    const {
      layoutViewport: { pageX, pageY }
    } = await this._client.send('Page.getLayoutMetrics')

    const clip = Object.assign({}, boundingBox)
    clip.x += pageX
    clip.y += pageY

    const imageData = await this._page.screenshot(
      Object.assign(
        {},
        {
          clip
        },
        options
      )
    )

    if (needsViewportReset) await this._page.setViewport(viewport)

    return imageData
  }

  /**
   * @param {string} elemId
   * @return {Promise<ElementHandle|undefined>}
   * @see https://developer.mozilla.org/en-US/docs/Web/API/Document/getElementById
   * @since chrome-remote-interface-extra
   */
  async getElementById (elemId) {
    const handle = await this.executionContext().evaluateHandle(
      (element, id) => element.getElementById(id),
      this,
      elemId
    )
    const element = handle.asElement()
    if (element) return element
    await handle.dispose()
    return null
  }

  /**
   * @param {string} selector
   * @return {Promise<ElementHandle|undefined>}
   */
  async $ (selector) {
    const handle = await this.executionContext().evaluateHandle(
      (element, selector) => element.querySelector(selector),
      this,
      selector
    )
    const element = handle.asElement()
    if (element) return element
    await handle.dispose()
    return null
  }

  /**
   * @param {string} selector
   * @return {Promise<Array<ElementHandle>>}
   */
  async $$ (selector) {
    const arrayHandle = await this.executionContext().evaluateHandle(
      (element, selector) => element.querySelectorAll(selector),
      this,
      selector
    )
    const properties = await arrayHandle.getProperties()
    await arrayHandle.dispose()
    const result = []
    for (const property of properties.values()) {
      const elementHandle = property.asElement()
      if (elementHandle) result.push(elementHandle)
    }
    return result
  }

  /**
   * @param {string} selector
   * @param {Function|String} pageFunction
   * @param {...*} args
   * @return {Promise<Object|undefined>}
   */
  async $eval (selector, pageFunction, ...args) {
    const elementHandle = await this.$(selector)
    if (!elementHandle) {
      throw new Error(
        `Error: failed to find element matching selector "${selector}"`
      )
    }
    const result = await this.executionContext().evaluate(
      pageFunction,
      elementHandle,
      ...args
    )
    await elementHandle.dispose()
    return result
  }

  /**
   * @param {string} selector
   * @param {Function|String} pageFunction
   * @param {...*} args
   * @return {Promise<Object|undefined>}
   */
  async $$eval (selector, pageFunction, ...args) {
    const arrayHandle = await this.executionContext().evaluateHandle(
      (element, selector) => Array.from(element.querySelectorAll(selector)),
      this,
      selector
    )

    const result = await this.executionContext().evaluate(
      pageFunction,
      arrayHandle,
      ...args
    )
    await arrayHandle.dispose()
    return result
  }

  /**
   * @param {string} expression
   * @return {Promise<Array<ElementHandle>>}
   */
  async $x (expression) {
    const arrayHandle = await this.executionContext().evaluateHandle(
      (element, expression) => {
        const document = element.ownerDocument || element
        const iterator = document.evaluate(
          expression,
          element,
          null,
          XPathResult.ORDERED_NODE_ITERATOR_TYPE
        )
        const array = []
        let item
        while ((item = iterator.iterateNext())) array.push(item)
        return array
      },
      this,
      expression
    )
    const properties = await arrayHandle.getProperties()
    await arrayHandle.dispose()
    const result = []
    for (const property of properties.values()) {
      const elementHandle = property.asElement()
      if (elementHandle) result.push(elementHandle)
    }
    return result
  }

  /**
   * Scrolls the element into view.
   * @param {boolean|Object} [scrollHow = {block: 'center', inline: 'center', behavior: 'instant'}] - How to scroll the element into view
   * @return {Promise<void>}
   * @see https://developer.mozilla.org/en-US/docs/Web/API/Element/scrollIntoView
   * @since chrome-remote-interface-extra
   */
  async scrollIntoView (scrollHow) {
    const scrollIntoViewOptions = scrollHow || {
      block: 'center',
      inline: 'center',
      behavior: 'instant'
    }
    switch (typeof scrollIntoViewOptions) {
      case 'boolean':
      case 'object':
        break
      default:
        throw new Error(
          `The scrollHow param can only be an object or boolean but you supplied ${typeof scrollHow}. `
        )
    }
    await this.executionContext().evaluate(
      async (element, scrollIntoViewOpts) => {
        if (!element.isConnected) return 'Node is detached from document'
        if (
          !element.scrollIntoView ||
          typeof element.scrollIntoView !== 'function'
        ) {
          return 'Node is not of type Element or does not have the scrollIntoView function'
        }
        element.scrollIntoView(scrollIntoViewOpts)
        return false
      },
      this,
      scrollIntoViewOptions
    )
  }

  /**
   * Conditionally scrolls the element into view.
   * @param {boolean|Object} [scrollHow = {block: 'center', inline: 'center', behavior: 'instant'}] - How to scroll the element into view
   * @return {Promise<void>}
   * @see https://developer.mozilla.org/en-US/docs/Web/API/Element/scrollIntoView
   * @since chrome-remote-interface-extra
   * @public
   */
  async scrollIntoViewIfNeeded (scrollHow) {
    let scrollIntoViewOptions
    if (typeof scrollHow === 'boolean') {
      scrollIntoViewOptions = scrollHow
    } else {
      scrollIntoViewOptions = scrollHow || {
        block: 'center',
        inline: 'center',
        behavior: 'instant'
      }
    }
    const error = await this.executionContext().evaluate(
      async (element, pageJavascriptEnabled, scrollIntoViewOpts) => {
        if (!element.isConnected) return 'Node is detached from document'
        if (
          !element.scrollIntoView ||
          typeof element.scrollIntoView !== 'function'
        ) {
          return 'Node is not of type Element or does not have the scrollIntoView function'
        }
        // force-scroll if page's javascript is disabled.
        if (!pageJavascriptEnabled) {
          element.scrollIntoView(scrollIntoViewOpts)
          return false
        }
        const visibleRatio = await new Promise(resolve => {
          const observer = new IntersectionObserver(entries => {
            resolve(entries[0].intersectionRatio)
            observer.disconnect()
          })
          observer.observe(element)
        })
        if (visibleRatio !== 1.0) {
          element.scrollIntoView(scrollIntoViewOpts)
        }
        return false
      },
      this,
      this._page.javascriptEnabled,
      scrollIntoViewOptions
    )
    if (error) throw new Error(error)
  }

  /**
   * @return {Promise<{x: number, y: number}>}
   */
  async _clickablePoint () {
    const result = await this._client
      .send('DOM.getContentQuads', {
        objectId: this._remoteObject.objectId
      })
      .catch(debugError)
    if (!result || !result.quads.length) {
      throw new Error('Node is either not visible or not an HTMLElement')
    }
    // Filter out quads that have too small area to click into.
    const quads = []
    const resultQuads = result.quads
    for (let i = 0; i < resultQuads.length; i++) {
      const _quads = this._fromProtocolQuad(resultQuads[i])
      if (computeQuadArea(_quads) > 1) {
        quads.push(_quads)
      }
    }
    if (!quads.length) {
      throw new Error('Node is either not visible or not an HTMLElement')
    }
    // Return the middle point of the first quad.
    const quad = quads[0]
    let x = 0
    let y = 0
    for (let i = 0; i < quad.length; i++) {
      const point = quad[i]
      x += point.x
      y += point.y
    }
    const clickablePoint = {
      x: x / 4,
      y: y / 4
    }
    return clickablePoint
  }

  /**
   * @return {Promise<void|Object>}
   */
  _getBoxModel () {
    return this._client
      .send('DOM.getBoxModel', {
        objectId: this._remoteObject.objectId
      })
      .catch(error => debugError(error))
  }

  /**
   * @param {Array<number>} quad
   * @return {Array<{x: number, y: number}>}
   */
  _fromProtocolQuad (quad) {
    return [
      { x: quad[0], y: quad[1] },
      { x: quad[2], y: quad[3] },
      { x: quad[4], y: quad[5] },
      { x: quad[6], y: quad[7] }
    ]
  }
}

exports.ElementHandle = ElementHandle

function computeQuadArea (quad) {
  // Compute sum of all directed areas of adjacent triangles
  // https://en.wikipedia.org/wiki/Polygon#Simple_polygons
  let area = 0
  for (let i = 0; i < quad.length; ++i) {
    const p1 = quad[i]
    const p2 = quad[(i + 1) % quad.length]
    area += (p1.x * p2.y - p2.x * p1.y) / 2
  }
  return Math.abs(area)
}

/**
 * @typedef {Object} BoxModel
 * @property {Array<{x: number, y: number}>} content
 * @property {Array<{x: number, y: number}>} padding
 * @property {Array<{x: number, y: number}>} border
 * @property {Array<{x: number, y: number}>} margin
 * @property {number} width
 * @property {number} height
 */

const { helper, assert } = require('./helper')
const Events = require('./Events')
const { TimeoutError } = require('./Errors')

/**
 * An utility class that watches the supplied frame and its children (if any)
 * to determine if they reach the specified lifecycle(s)
 *
 * Lifecycle mapping in the form of supplied to CDP value:
 *  - load: load
 *  - domcontentloaded: DOMContentLoaded
 *  - networkIdle: networkIdle
 *  - networkAlmostIdle: networkAlmostIdle
 *  - networkidle0: networkIdle
 *  - networkidle2: networkAlmostIdle
 */
class LifecycleWatcher {
  /**
   * @param {!FrameManager} frameManager - The frame manager for the page containing the frame being navigated
   * @param {!Frame} frame - The frame being navigated
   * @param {string|Array<string>} waitUntil - The lifecycle(s) desired to be obtained by the frame and its children
   * @param {number} [timeout] - An optional timeout value
   */
  constructor (frameManager, frame, waitUntil, timeout) {
    let waitUntilArray
    if (Array.isArray(waitUntil)) {
      waitUntilArray = waitUntil.slice()
    } else if (typeof waitUntil === 'string') {
      waitUntilArray = [waitUntil]
    }

    /**
     * @type {Array<string>}
     * @private
     */
    this._expectedLifecycle = waitUntilArray.map(value => {
      const protocolEvent = protocolLifecycle[value]
      assert(protocolEvent, 'Unknown value for options.waitUntil: ' + value)
      return protocolEvent
    })

    /**
     * @type {!FrameManager}
     * @private
     */
    this._frameManager = frameManager

    /**
     * @type {?NetworkManager}
     * @private
     */
    this._networkManager = frameManager._networkManager

    /**
     * @type {!Frame}
     * @private
     */
    this._frame = frame

    /**
     * @type {string}
     * @private
     */
    this._initialLoaderId = frame._loaderId

    /**
     * @type {number}
     * @private
     */
    this._timeout = timeout
    /** @type {?Request} */
    this._navigationRequest = null

    /**
     * @type {{emitter: !EventEmitter, eventName: (string|symbol), handler: (function(*))}[]}
     * @private
     */
    this._eventListeners = [
      helper.addEventListener(
        frameManager._client,
        frameManager._client.$$disconnectEvent || Events.CRIClient.Disconnected,
        this._onConnectionDisconnected.bind(this)
      ),
      helper.addEventListener(
        this._frameManager,
        Events.FrameManager.LifecycleEvent,
        this._checkLifecycleComplete.bind(this)
      ),
      helper.addEventListener(
        this._frameManager,
        Events.FrameManager.FrameNavigatedWithinDocument,
        this._navigatedWithinDocument.bind(this)
      ),
      helper.addEventListener(
        this._frameManager,
        Events.FrameManager.FrameDetached,
        this._onFrameDetached.bind(this)
      )
    ]

    if (this._networkManager) {
      this._eventListeners.push(
        helper.addEventListener(
          this._networkManager,
          Events.NetworkManager.Request,
          this._onRequest.bind(this)
        )
      )
    }

    /**
     * A Promise that resolves if the frame navigated within the same document (History.pushState etc)
     * @type {Promise<*>}
     */
    this._sameDocumentNavigationPromise = new Promise(resolve => {
      this._sameDocumentNavigationCompleteCallback = resolve
    })

    /**
     * A Promise that resolves if the frame being navigated reached the expected lifecycle
     * @type {Promise<*>}
     */
    this._lifecyclePromise = new Promise(resolve => {
      this._lifecycleCallback = resolve
    })

    /**
     * A Promise that resolves if the frame being navigated navigated to a new page
     * @type {Promise<*>}
     */
    this._newDocumentNavigationPromise = new Promise(resolve => {
      this._newDocumentNavigationCompleteCallback = resolve
    })

    /**
     * A Promise that resolves if the frame being navigated did not navigate within the
     * supplied timeout if any
     * @type {Promise<*>}
     */
    this._timeoutPromise = this._createTimeoutPromise()

    /**
     * A Promise that resolves if the the watcher is terminated
     * @type {Promise<*>}
     */
    this._terminationPromise = new Promise(resolve => {
      this._terminationCallback = resolve
    })

    this._checkLifecycleComplete()
  }

  _onConnectionDisconnected () {
    this._terminate(
      new Error('Navigation failed because browser has disconnected!')
    )
  }

  /**
   * @param {!Request} request
   */
  _onRequest (request) {
    if (request.frame() !== this._frame || !request.isNavigationRequest()) {
      return
    }
    this._navigationRequest = request
  }

  /**
   * @param {!Frame} frame
   */
  _onFrameDetached (frame) {
    if (this._frame === frame) {
      this._terminationCallback.call(
        null,
        new Error('Navigating frame was detached')
      )
      return
    }
    this._checkLifecycleComplete()
  }

  /**
   * @return {?Response}
   */
  navigationResponse () {
    return this._navigationRequest ? this._navigationRequest.response() : null
  }

  /**
   * @param {!Error} error
   */
  _terminate (error) {
    this._terminationCallback.call(null, error)
  }

  /**
   * @return {Promise<Error|undefined>}
   */
  sameDocumentNavigationPromise () {
    return this._sameDocumentNavigationPromise
  }

  /**
   * @return {Promise<Error|undefined>}
   */
  newDocumentNavigationPromise () {
    return this._newDocumentNavigationPromise
  }

  /**
   * @return {Promise<*>}
   */
  lifecyclePromise () {
    return this._lifecyclePromise
  }

  /**
   * @return {Promise<Error|undefined>}
   */
  timeoutOrTerminationPromise () {
    return Promise.race([this._timeoutPromise, this._terminationPromise])
  }

  /**
   * @return {Promise<Error|undefined>}
   */
  _createTimeoutPromise () {
    if (!this._timeout) return new Promise(() => {})
    const errorMessage =
      'Navigation Timeout Exceeded: ' + this._timeout + 'ms exceeded'

    return new Promise(resolve => {
      this._maximumTimer = setTimeout(resolve, this._timeout)
    }).then(() => new TimeoutError(errorMessage))
  }

  /**
   * @param {!Frame} frame
   */
  _navigatedWithinDocument (frame) {
    if (frame !== this._frame) return
    this._hasSameDocumentNavigation = true
    this._checkLifecycleComplete()
  }

  /**
   * Checks the frame being navigated and all its child frames for the expected lifecycle(s)
   * @private
   */
  _checkLifecycleComplete () {
    // We expect navigation to commit.
    if (!checkLifecycle(this._frame, this._expectedLifecycle)) return
    this._lifecycleCallback()
    if (
      this._frame._loaderId === this._initialLoaderId &&
      !this._hasSameDocumentNavigation
    ) {
      return
    }
    if (this._hasSameDocumentNavigation) {
      this._sameDocumentNavigationCompleteCallback()
    }
    if (this._frame._loaderId !== this._initialLoaderId) {
      this._newDocumentNavigationCompleteCallback()
    }
  }

  /**
   * Dispose of the LifecycleWatcher (i.e. clean up)
   */
  dispose () {
    helper.removeEventListeners(this._eventListeners)
    clearTimeout(this._maximumTimer)
  }
}

/**
 * @param {!Frame} frame
 * @param {Array<string>} expectedLifecycle
 * @return {boolean}
 */
function checkLifecycle (frame, expectedLifecycle) {
  let i = 0
  for (; i < expectedLifecycle.length; i++) {
    if (!frame._lifecycleEvents.has(expectedLifecycle[i])) return false
  }
  const childFrames = frame.childFrames()
  for (i = 0; i < childFrames.length; i++) {
    if (!checkLifecycle(childFrames[i], expectedLifecycle)) return false
  }
  return true
}

const protocolLifecycle = {
  load: 'load',
  domcontentloaded: 'DOMContentLoaded',
  networkIdle: 'networkIdle',
  networkAlmostIdle: 'networkAlmostIdle',
  networkidle0: 'networkIdle',
  networkidle2: 'networkAlmostIdle'
}

/**
 * @type {LifecycleWatcher}
 */
module.exports = LifecycleWatcher

const util = require('util')

/**
 * @template K
 * @template V
 */
class Multimap {
  constructor () {
    /**
     * @type {Map<K, V>}
     * @private
     */
    this._map = new Map()
  }

  /**
   * @param {K} key
   * @param {V} value
   */
  set (key, value) {
    let set = this._map.get(key)
    if (!set) {
      set = new Set()
      this._map.set(key, set)
    }
    set.add(value)
  }

  /**
   * @param {K} key
   * @return {!Set<V>}
   */
  get (key) {
    let result = this._map.get(key)
    if (!result) result = new Set()
    return result
  }

  /**
   * @param {K} key
   * @return {boolean}
   */
  has (key) {
    return this._map.has(key)
  }

  /**
   * @param {K} key
   * @param {V} value
   * @return {boolean}
   */
  hasValue (key, value) {
    const set = this._map.get(key)
    if (!set) return false
    return set.has(value)
  }

  /**
   * @return {number}
   */
  get size () {
    return this._map.size
  }

  /**
   * @param {K} key
   * @param {V} value
   * @return {boolean}
   */
  delete (key, value) {
    const values = this.get(key)
    const result = values.delete(value)
    if (!values.size) this._map.delete(key)
    return result
  }

  /**
   * @param {K} key
   */
  deleteAll (key) {
    this._map.delete(key)
  }

  /**
   * @param {K} key
   * @return {V}
   */
  firstValue (key) {
    const set = this._map.get(key)
    if (!set) return null
    return set.values().next().value
  }

  /**
   * @return {K}
   */
  firstKey () {
    return this._map.keys().next().value
  }

  /**
   * @return {Array<V>}
   */
  values () {
    const result = []
    for (const key of this._map.keys()) {
      result.push(...Array.from(this._map.get(key).values()))
    }
    return result
  }

  /**
   * @return {Array<K>}
   */
  keys () {
    return Array.from(this._map.keys())
  }

  clear () {
    this._map.clear()
  }

  // eslint-disable-next-line space-before-function-paren
  [util.inspect.custom](depth, options) {
    if (depth < 0) {
      return options.stylize('[Multimap]', 'special')
    }

    const newOptions = Object.assign({}, options, {
      depth: options.depth == null ? null : options.depth - 1
    })
    const inner = util.inspect(this._map, newOptions)
    return `${options.stylize('Multimap', 'special')} ${inner}`
  }
}

module.exports = Multimap

const util = require('util')
const Events = require('./Events')
const Page = require('./page/Page')
const { helper } = require('./helper')
/** @ignore */
const {
  closeTarget,
  exposeCDPOnTarget,
  getWindowForTarget,
  setWindowBounds
} = require('./__shared')

/**
 * @typedef {Object} TargetInit
 * @property {Object} targetInfo
 * @property {?BrowserContext} [browserContext]
 * @property {?function():Promise<CDPSession>} [sessionFactory]
 * @property {?PageInitOptions} [pageOpts]
 * @property {Chrome|CDPSession|CRIConnection} [client]
 */

/**
 * @see https://chromedevtools.github.io/devtools-protocol/tot/Target
 */
class Target {
  /**
   * @param {!TargetInit} targetInit
   */
  constructor ({
    targetInfo,
    browserContext,
    sessionFactory,
    pageOpts,
    client
  }) {
    /**
     * @type {!Object}
     * @private
     */
    this._targetInfo = targetInfo

    /**
     * @type {?BrowserContext}
     * @private
     */
    this._browserContext = browserContext

    /**
     * @type {Chrome|CDPSession|CRIConnection|undefined}
     */
    this._client = client

    /**
     * @type {string}
     * @private
     */
    this._targetId = targetInfo.targetId

    /**
     * @type {function(): Promise<CDPSession>}
     * @private
     */
    this._sessionFactory = sessionFactory || this.__sessionFactory.bind(this)

    /**
     * @ignore
     * @type {?Array<{emitter: !EventEmitter, eventName: (string|symbol), handler: function(*)}>}
     * @private
     */
    this.__eventListeners = null
    if (!this._browserContext) {
      this.__eventListeners = [
        helper.addEventListener(
          this._client,
          'Target.targetInfoChanged',
          this.__onTargetInfoChanged
        ),
        helper.addEventListener(
          this._client,
          'Target.targetDestroyed',
          this.__onTargetDestroyed
        )
      ]
    }

    /**
     * @type {PageInitOptions}
     * @private
     */
    this._pageOpts = pageOpts || {}
    /** @type {?Promise<Page>} */
    this._pagePromise = null
    /** @type {Promise<boolean>} */
    this._initializedPromise = new Promise(
      resolve => (this._initializedCallback = resolve)
    ).then(async success => {
      if (!success) return false
      const opener = this.opener()
      if (!opener || !opener._pagePromise || this.type() !== 'page') return true
      const openerPage = await opener._pagePromise
      if (!openerPage.listenerCount(Events.Page.Popup)) return true
      const popupPage = await this.page()
      openerPage.emit(Events.Page.Popup, popupPage)
      return true
    })
    /** @type {Promise<*>} */
    this._isClosedPromise = new Promise(
      resolve => (this._closedCallback = resolve)
    )
    /** @type {boolean} */
    this._isInitialized =
      this._targetInfo.type !== 'page' || this._targetInfo.url !== ''
    if (this._isInitialized) this._initializedCallback(true)
  }

  /**
   * @return {string}
   */
  id () {
    return this._targetInfo.targetId
  }

  /**
   * @return {string}
   */
  url () {
    return this._targetInfo.url
  }

  /**
   * @return {"page"|"background_page"|"service_worker"|"other"|"browser"}
   */
  type () {
    const type = this._targetInfo.type
    if (
      type === 'page' ||
      type === 'background_page' ||
      type === 'service_worker' ||
      type === 'browser'
    ) {
      return type
    }
    return 'other'
  }

  /**
   * @return {?string}
   */
  openerId () {
    return this._targetInfo.openerId
  }

  /**
   * @return {Promise<CDPSession>}
   */
  createCDPSession () {
    return this._sessionFactory()
  }

  /**
   * Returns T/F indicating if the target is for a page
   * @return {boolean}
   */
  isPageTarget () {
    if (this._targetInfo) return this._targetInfo.type === 'page'
    return false
  }

  /**
   * Returns T/F indicating if the target is for a background page
   * @return {boolean}
   */
  isBackgroundPageTarget () {
    if (this._targetInfo) return this._targetInfo.type === 'background_page'
    return false
  }

  /**
   * Returns T/F indicating if the target is for a browser
   * @return {boolean}
   */
  isBrowserTarget () {
    if (this._targetInfo) return this._targetInfo.type === 'browser'
    return false
  }

  /**
   * Returns T/F indicating if the target is for a service worker
   * @return {boolean}
   */
  isServiceWorkerTarget () {
    if (this._targetInfo) return this._targetInfo.type === 'service_worker'
    return false
  }

  /**
   * @return {?Browser}
   */
  browser () {
    if (!this._browserContext) return null
    return this._browserContext.browser()
  }

  /**
   * @return {?BrowserContext}
   */
  browserContext () {
    if (!this._browserContext) return null
    return this._browserContext
  }

  /**
   * @return {?Target}
   */
  opener () {
    const { openerId } = this._targetInfo
    if (!openerId) return null
    const browser = this.browser()
    if (browser) return browser.getTargetById(openerId)
    return null
  }

  /**
   * @return {Promise<Page|undefined>}
   */
  page () {
    if (
      (this._targetInfo.type === 'page' ||
        this._targetInfo.type === 'background_page') &&
      !this._pagePromise
    ) {
      this._pagePromise = this._sessionFactory().then(client =>
        Page.create(client, Object.assign({ target: this }, this._pageOpts))
      )
    }
    return this._pagePromise
  }

  /**
   * Closes the target. If the target is a page that gets closed too.
   * @return {Promise<boolean>}
   */
  close () {
    if (this._browserContext) {
      return this._browserContext.closeTarget(this._targetInfo.targetId)
    }
    return closeTarget(this._client, this._targetInfo.targetId)
  }

  /**
   * Get the browser window id and bounds for this target
   * @return {Promise<{bounds: WindowBounds, windowId: number}>}
   */
  getWindowBounds () {
    if (this._browserContext) {
      return this._browserContext.getWindowForTarget(this._targetInfo.targetId)
    }
    return getWindowForTarget(this._client, this._targetInfo.targetId)
  }

  /**
   * Set position and/or size of the browser window. EXPERIMENTAL
   * @param {number} windowId - An browser window id
   * @param {WindowBounds} bounds - New window bounds. The 'minimized', 'maximized' and 'fullscreen' states cannot be combined with 'left', 'top', 'width' or 'height'. Leaves unspecified fields unchanged.
   * @return {Promise<void>}
   * @see https://chromedevtools.github.io/devtools-protocol/tot/Browser#method-setWindowBounds
   */
  async setWindowBounds (windowId, bounds) {
    if (this._browserContext) {
      await this._browserContext.setWindowBounds(windowId, bounds)
    }
    await setWindowBounds(this._client, windowId, bounds)
  }

  /**
   * Inject object to the target's main frame that provides a communication channel with browser target.
   *
   * Injected object will be available as window[bindingName].
   *
   * The object has the following API:
   *  * binding.send(json) - a method to send messages over the remote debugging protocol
   *  * binding.onmessage = json => handleMessage(json) - a callback that will be called for the protocol notifications and command responses.
   *
   * EXPERIMENTAL
   * @param {string} [bindingName] - Binding name, 'cdp' if not specified
   * @return {Promise<void>}
   * @see https://chromedevtools.github.io/devtools-protocol/tot/Target#method-exposeDevToolsProtocol
   */
  async exposeDevToolsProtocol (bindingName) {
    if (this._browserContext) {
      await this._browserContext.exposeCDPOnTarget(
        this._targetInfo.targetId,
        bindingName || undefined
      )
      return
    }
    await exposeCDPOnTarget(
      this._client,
      this._targetInfo.targetId,
      bindingName
    )
  }

  /**
   * @return {Object}
   */
  toJSON () {
    return this._targetInfo
  }

  /**
   * @param {!Object} targetInfo
   */
  _targetInfoChanged (targetInfo) {
    this._targetInfo = targetInfo

    if (
      !this._isInitialized &&
      (this._targetInfo.type !== 'page' || this._targetInfo.url !== '')
    ) {
      this._isInitialized = true
      this._initializedCallback(true)
    }
  }

  /**
   * @ignore
   * @param {!Object} event
   */
  __onTargetInfoChanged (event) {
    if (event.targetInfo.targetId === this._targetInfo.targetId) {
      this._targetInfoChanged(event.targetInfo)
    }
  }

  /**
   * @ignore
   * @param {!Object} event
   */
  __onTargetDestroyed (event) {
    if (event.targetInfo.targetId === this._targetInfo.targetId) {
      this._initializedCallback(false)
      this._closedCallback()
      helper.removeEventListeners(this.__eventListeners)
    }
  }

  /**
   * @ignore
   * @return {Promise<CDPSession>|MSMediaKeySession|MediaKeySession}
   * @private
   */
  __sessionFactory () {
    return this._client.createSession(this._targetInfo)
  }

  /** @ignore */
  // eslint-disable-next-line space-before-function-paren
  [util.inspect.custom](depth, options) {
    if (depth < 0) {
      return options.stylize('[Target]', 'special')
    }

    const newOptions = Object.assign({}, options, {
      depth: options.depth == null ? null : options.depth - 1
    })
    const inner = util.inspect(this._targetInfo, newOptions)
    return `${options.stylize('Target', 'special')} ${inner}`
  }
}

/**
 * @type {Target}
 */
module.exports = Target

