   */
  async up (options = {}) {
    const { button = 'left', clickCount = 1 } = options
    this._button = 'none'
    await this._client.send('Input.dispatchMouseEvent', {
      type: 'mouseReleased',
      button,
      x: this._x,
      y: this._y,
      modifiers: this._keyboard._modifiers,
