  /**
   * @param {!ScreenshotOptions=} options
   * @return {Promise<Buffer|!String>}
   */
  async screenshot (options = {}) {
    let screenshotType = null
    // options.type takes precedence over inferring the type from options.path
    // because it may be a 0-length file with no extension created beforehand (i.e. as a temp file).
    if (options.type) {
      assert(
        options.type === 'png' || options.type === 'jpeg',
        'Unknown options.type value: ' + options.type
      )
      screenshotType = options.type
    } else if (options.path) {
      const mimeType = mime.getType(options.path)
      if (mimeType === 'image/png') screenshotType = 'png'
      else if (mimeType === 'image/jpeg') screenshotType = 'jpeg'
      assert(screenshotType, 'Unsupported screenshot mime type: ' + mimeType)
    }

    if (!screenshotType) screenshotType = 'png'

    if (options.quality) {
      assert(
        screenshotType === 'jpeg',
        'options.quality is unsupported for the ' +
          screenshotType +
          ' screenshots'
      )
      assert(
        typeof options.quality === 'number',
        'Expected options.quality to be a number but found ' +
          typeof options.quality
      )
      assert(
        Number.isInteger(options.quality),
        'Expected options.quality to be an integer'
      )
      assert(
        options.quality >= 0 && options.quality <= 100,
        'Expected options.quality to be between 0 and 100 (inclusive), got ' +
          options.quality
      )
    }
    assert(
      !options.clip || !options.fullPage,
      'options.clip and options.fullPage are exclusive'
    )
    if (options.clip) {
      assert(
        typeof options.clip.x === 'number',
        'Expected options.clip.x to be a number but found ' +
          typeof options.clip.x
      )
      assert(
        typeof options.clip.y === 'number',
        'Expected options.clip.y to be a number but found ' +
          typeof options.clip.y
      )
      assert(
        typeof options.clip.width === 'number',
        'Expected options.clip.width to be a number but found ' +
          typeof options.clip.width
      )
      assert(
        typeof options.clip.height === 'number',
        'Expected options.clip.height to be a number but found ' +
          typeof options.clip.height
      )
      assert(
        options.clip.width !== 0,
        'Expected options.clip.width not to be 0.'
      )
      assert(
        options.clip.height !== 0,
        'Expected options.clip.width not to be 0.'
      )
    }
    return this._screenshotTaskQueue.postTask(
      this._screenshotTask.bind(this, screenshotType, options)
    )
  }

  /**
   * @param {!PDFOptions=} options
   * @return {Promise<Buffer>}
   */
  async pdf (options = {}) {
    const {
      scale = 1,
      displayHeaderFooter = false,
      headerTemplate = '',
      footerTemplate = '',
      printBackground = false,
      landscape = false,
      pageRanges = '',
      preferCSSPageSize = false,
      margin = {},
      path = null
    } = options

    let paperWidth = 8.5
    let paperHeight = 11
    if (options.format) {
      const format = Page.PaperFormats[options.format.toLowerCase()]
      assert(format, 'Unknown paper format: ' + options.format)
      paperWidth = format.width
      paperHeight = format.height
    } else {
      paperWidth = convertPrintParameterToInches(options.width) || paperWidth
      paperHeight = convertPrintParameterToInches(options.height) || paperHeight
    }

    const marginTop = convertPrintParameterToInches(margin.top) || 0
    const marginLeft = convertPrintParameterToInches(margin.left) || 0
    const marginBottom = convertPrintParameterToInches(margin.bottom) || 0
    const marginRight = convertPrintParameterToInches(margin.right) || 0

    const result = await this._client.send('Page.printToPDF', {
      landscape,
      displayHeaderFooter,
      headerTemplate,
      footerTemplate,
      printBackground,
      scale,
      paperWidth,
      paperHeight,
      marginTop,
      marginBottom,
      marginLeft,
      marginRight,
      pageRanges,
      preferCSSPageSize
    })
    const buffer = Buffer.from(result.data, 'base64')
    if (path !== null) await fs.writeFile(path, buffer)
    return buffer
  }

  /**
   * @param {!{runBeforeUnload: (boolean|undefined)}=} options
   */
  async close (options = { runBeforeUnload: undefined }) {
    const runBeforeUnload = !!options.runBeforeUnload
    if (runBeforeUnload) {
      await this._client.send('Page.close')
    } else if (this._target) {
      await this._target.close()
      await this._target._isClosedPromise
    }
  }

  /**
   * @param {"png"|"jpeg"} format
   * @param {!ScreenshotOptions=} options
   * @return {Promise<Buffer|!String>}
   */
  async _screenshotTask (format, options) {
    if (this._target) {
      await this._client.send('Target.activateTarget', {
        targetId: this._target.id()
      })
    }
    let clip = options.clip ? processClip(options.clip) : undefined

    if (options.fullPage) {
      const metrics = await this.getLayoutMetrics()
      const width = Math.ceil(metrics.contentSize.width)
      const height = Math.ceil(metrics.contentSize.height)

      // Overwrite clip for full page at all times.
      clip = { x: 0, y: 0, width, height, scale: 1 }
      const { isMobile = false, deviceScaleFactor = 1, isLandscape = false } =
        this._viewport || {}
      /** @type {!Object} */
      const screenOrientation = isLandscape
        ? { angle: 90, type: 'landscapePrimary' }
        : { angle: 0, type: 'portraitPrimary' }
      await this._client.send('Emulation.setDeviceMetricsOverride', {
        mobile: isMobile,
        width,
        height,
        deviceScaleFactor,
        screenOrientation
      })
    }
    const shouldSetDefaultBackground =
      options.omitBackground && format === 'png'
    if (shouldSetDefaultBackground) {
      await this._emulationManager.setDefaultBackgroundColorOverride({
        r: 0,
        g: 0,
        b: 0,
        a: 0
      })
    }
    const result = await this._client.send('Page.captureScreenshot', {
      format,
      quality: options.quality,
      clip
    })
    if (shouldSetDefaultBackground) {
      await this._emulationManager.setDefaultBackgroundColorOverride()
    }

    if (options.fullPage && this._viewport) {
      await this.setViewport(this._viewport)
    }

    const buffer =
      options.encoding === 'base64'
        ? result.data
        : Buffer.from(result.data, 'base64')
    if (options.path) await fs.writeFile(options.path, buffer)
    return buffer

    function processClip (clip) {
      const x = Math.round(clip.x)
      const y = Math.round(clip.y)
      const width = Math.round(clip.width + clip.x - x)
      const height = Math.round(clip.height + clip.y - y)
      return { x, y, width, height, scale: 1 }
    }
  }

  /**
   * @param delta
   * @param {!{timeout?: number, waitUntil?: string|Array<string>}=} options
   * @return {Promise<Response|undefined>}
   */
  async _go (delta, options) {
    const history = await this.getNavigationHistory()
    const entry = history.entries[history.currentIndex + delta]
    if (!entry) return null
    const [response] = await Promise.all([
      this.waitForNavigation(options),
      this.navigateToHistoryEntry(entry.id)
    ])
    return response
  }

  /**
   * @param {!Object} event
   */
  async _onConsoleAPI (event) {
    if (event.executionContextId === 0) {
      // DevTools protocol stores the last 1000 console messages. These
      // messages are always reported even for removed execution contexts. In
      // this case, they are marked with executionContextId = 0 and are
      // reported upon enabling Runtime agent.
      //
      // Ignore these messages since:
      // - there's no execution context we can use to operate with message
      //   arguments
      // - these messages are reported before Puppeteer clients can subscribe
      //   to the 'console'
      //   page event.
      //
      // @see https://github.com/GoogleChrome/puppeteer/issues/3865
      return
    }
    const context = this._frameManager.executionContextById(
      event.executionContextId
    )
    if (!this.listenerCount(Events.Page.Console)) {
      event.args.forEach(arg => createJSHandle(context, arg).dispose())
      return
    }
    const message = new ConsoleMessage(event, { context })
    this.emit(Events.Page.Console, message)
  }

  /**
   * @param {!Object} event
   */
  async _onBindingCalled (event) {
    const { name, seq, args } = JSON.parse(event.payload)
    let expression = null
    try {
      const result = await this._pageBindings.get(name)(...args)
      expression = helper.evaluationString(deliverResult, name, seq, result)
    } catch (error) {
      if (error instanceof Error) {
        expression = helper.evaluationString(
          deliverError,
          name,
          seq,
          error.message,
          error.stack
        )
      } else {
        expression = helper.evaluationString(
          deliverErrorValue,
          name,
          seq,
          error
        )
      }
    }
    this._client
      .send('Runtime.evaluate', {
        expression,
        contextId: event.executionContextId
      })
      .catch(debugError)

    /**
     * @param {string} name
     * @param {number} seq
     * @param {*} result
     */
    function deliverResult (name, seq, result) {
      window[name]['callbacks'].get(seq).resolve(result)
      window[name]['callbacks'].delete(seq)
    }

    /**
     * @param {string} name
     * @param {number} seq
     * @param {string} message
     * @param {string} stack
     */
    function deliverError (name, seq, message, stack) {
      const error = new Error(message)
      error.stack = stack
      window[name]['callbacks'].get(seq).reject(error)
      window[name]['callbacks'].delete(seq)
    }

    /**
     * @param {string} name
     * @param {number} seq
     * @param {*} value
     */
    function deliverErrorValue (name, seq, value) {
      window[name]['callbacks'].get(seq).reject(value)
      window[name]['callbacks'].delete(seq)
    }
  }

  _onTargetCrashed () {
    this.emit('error', new Error('Page crashed!'))
    this.emit(Events.Page.Crashed)
  }

  /**
   * @param {!Object} event
   */
  _onLogEntryAdded (event) {
    if (event.entry.source !== 'worker') {
      this.emit(Events.Page.LogEntry, new LogEntry(event.entry))
    }
    if (event.entry.args) {
      return Promise.all(
        event.entry.args.map(arg => helper.releaseObject(this._client, arg))
      )
    }
  }

  /**
   * @param {!Object} event
   */
  _onCertificateError (event) {
    if (!this._ignoreHTTPSErrors) return
    this._client
      .send('Security.handleCertificateError', {
        eventId: event.eventId,
        action: 'continue'
      })
      .catch(debugError)
  }

  /**
   * @param {!Object} event
   */
  _emitMetrics (event) {
    this.emit(Events.Page.Metrics, {
      title: event.title,
      metrics: this._buildMetricsObject(event.metrics)
    })
  }

  /**
   * @param {?Array<Object>} metrics
   * @return {!Metrics}
   */
  _buildMetricsObject (metrics) {
    const result = {}
    const _metrics = metrics || []
    for (let i = 0; i < _metrics.length; i++) {
      const metric = _metrics[i]
      result[metric.name] = metric.value
    }
    return result
  }

  /**
   * @param {!Object} exception
   */
  _handleException (exception) {
    const message = helper.getExceptionMessage(exception.exceptionDetails)
    const err = new Error(message)
    err.stack = '' // Don't report clientside error with a node stack attached
    this.emit(Events.Page.PageError, err)
  }

  _onDialog (event) {
    Dialog.assertKnownDialogType(event)
    const dialog = new Dialog(this._client, event)
    this.emit(Events.Page.Dialog, dialog)
  }

  __onClose () {
    this.emit(Events.Page.Close)
    this._closed = true
  }
  /**
   * @return {{frames: !FrameManager, javascriptEnabled: boolean, additionalDomains: EnabledExtras, workers: IterableIterator<Worker>, url: string, target: ?Target, network: !NetworkManager}}
   */
  toJSON () {
    return {
      url: this._frameManager.mainFrame().url(),
      target: this._target,
      frames: this._frameManager,
      network: this._networkManager,
      additionalDomains: this._additionalDomains,
      workers: this._workers.values(),
      javascriptEnabled: this._javascriptEnabled
    }
  }

  /** @ignore */
  // eslint-disable-next-line space-before-function-paren
  [util.inspect.custom](depth, options) {
    if (depth < 0) {
      return options.stylize('[Page]', 'special')
    }

    const newOptions = Object.assign({}, options, {
      depth: options.depth == null ? null : options.depth - 1
    })
    const inner = util.inspect(
      {
        url: this.url(),
        target: this._target,
        network: this._networkManager,
        frames: this._frameManager,
        workers: this._workerManager,
        databases: this._databaseManager,
        emulation: this._emulationManager,
        pageBindings: this._pageBindings,
        additionalDomains: this._additionalDomains,
        javascriptEnabled: this._javascriptEnabled,
        timeoutSettings: this._timeoutSettings
      },
      newOptions
    )
    return `${options.stylize('Page', 'special')} ${inner}`
  }
}

/** @type {!Set<string>} */
const supportedMetrics = new Set([
  'Timestamp',
  'Documents',
  'Frames',
  'JSEventListeners',
  'Nodes',
  'LayoutCount',
  'RecalcStyleCount',
  'LayoutDuration',
  'RecalcStyleDuration',
  'ScriptDuration',
  'TaskDuration',
  'JSHeapUsedSize',
  'JSHeapTotalSize'
])

/** @enum {!{width: number, height: number}} */
Page.PaperFormats = {
  letter: { width: 8.5, height: 11 },
  legal: { width: 8.5, height: 14 },
  tabloid: { width: 11, height: 17 },
  ledger: { width: 17, height: 11 },
  a0: { width: 33.1, height: 46.8 },
  a1: { width: 23.4, height: 33.1 },
  a2: { width: 16.5, height: 23.4 },
  a3: { width: 11.7, height: 16.5 },
  a4: { width: 8.27, height: 11.7 },
  a5: { width: 5.83, height: 8.27 },
  a6: { width: 4.13, height: 5.83 }
}

const unitToPixels = {
  px: 1,
  in: 96,
  cm: 37.8,
  mm: 3.78
}

/**
 * @param {(string|number|undefined)} parameter
 * @return {(number|undefined)}
 */
function convertPrintParameterToInches (parameter) {
  if (typeof parameter === 'undefined') return undefined
  let pixels
  if (helper.isNumber(parameter)) {
    // Treat numbers as pixel values to be aligned with phantom's paperSize.
    pixels = /** @type {number} */ (parameter)
  } else if (helper.isString(parameter)) {
    const text = /** @type {string} */ (parameter)
    let unit = text.substring(text.length - 2).toLowerCase()
    let valueText = ''
    if (unitToPixels.hasOwnProperty(unit)) {
      valueText = text.substring(0, text.length - 2)
    } else {
      // In case of unknown unit try to parse the whole parameter as number of pixels.
      // This is consistent with phantom's paperSize behavior.
      unit = 'px'
      valueText = text
    }
    const value = Number(valueText)
    assert(!isNaN(value), 'Failed to parse parameter value: ' + text)
    pixels = value * unitToPixels[unit]
  } else {
    throw new Error(
      'page.pdf() Cannot handle parameter type: ' + typeof parameter
    )
  }
  return pixels / 96
}

/**
 * @type {Page}
 */
module.exports = Page

/**
 * @type {Page}
 */
exports.Page = require('./Page')

/**
 * @type {LogEntry}
 */
exports.LogEntry = require('./LogEntry')

const util = require('util')
const { URL } = require('url')
const EventEmitter = require('eventemitter3')
const Events = require('../Events')
const { ExecutionContext } = require('../executionContext')
const { JSHandle } = require('../JSHandle')
const { debugError } = require('../helper')

const RunningStates = {
  stopped: 'stopped',
  starting: 'starting',
  running: 'running',
  stopping: 'stopping'
}

const StatusStates = {
  new: 'new',
  installing: 'installing',
  installed: 'installed',
  activating: 'activating',
  activated: 'activated',
  redundant: 'redundant'
}

/**
 * @see https://chromedevtools.github.io/devtools-protocol/tot/ServiceWorker
 * @since chrome-remote-interface-extra
 */
class ServiceWorker extends EventEmitter {
  /**
   * @param {WorkerManager} manager
   */
  constructor (manager) {
    super()
    /**
     * @type {WorkerManager}
     * @private
     */
    this._manager = manager

    /**
     * @type {ServiceWorkerInfo}
     * @private
     */
    this._info = {}

    /**
     * @type {?string}
     * @private
     */
    this._origin = null
  }

  /**
   *
   * @return {string}
   */
  versionId () {
    return this._info.versionId
  }

  /**
   *
   * @return {string}
   */
  registrationId () {
    return this._info.registrationId
  }

  /**
   *
   * @return {string}
   */
  scopeURL () {
    return this._info.scopeURL
  }

  /**
   *
   * @return {boolean}
   */
  isDeleted () {
    return this._info.isDeleted
  }

  /**
   *
   * @return {string}
   */
  runningStatus () {
    return this._info.runningStatus
  }

  /**
   * Returns the ServiceWorkers status
   * Possible status:
   *  - new
   *  - installing
   *  - installed
   *  - activating
   *  - activated
   *  - redundant
   * @return {string}
   */
  status () {
    return this._info.status
  }

  /**
   * @return {?number}
   */
  scriptLastModified () {
    return this._info.scriptLastModified
  }

  /**
   * @return {?number}
   */
  scriptResponseTime () {
    return this._info.scriptResponseTime
  }

  /**
   * @return {string}
   */
  targetId () {
    return this._info.targetId
  }

  /**
   * @return {?Array<string>}
   */
  controlledClients () {
    return this._info.controlledClients
  }

  /**
   * @return {boolean}
   */
  isStopped () {
    return this._info.runningStatus === RunningStates.stopped
  }

  /**
   * @return {boolean}
   */
  isStarting () {
    return this._info.runningStatus === RunningStates.starting
  }

  /**
   * @return {boolean}
   */
  isRunning () {
    return this._info.runningStatus === RunningStates.running
  }

  /**
   * @return {boolean}
   */
  isStopping () {
    return this._info.runningStatus === RunningStates.stopping
  }

  /**
   * @return {boolean}
   */
  isNew () {
    return this._info.status === StatusStates.new
  }

  /**
   * @return {boolean}
   */
  isInstalling () {
    return this._info.status === StatusStates.installing
  }

  /**
   * @return {boolean}
   */
  isInstalled () {
    return this._info.status === StatusStates.installed
  }

  /**
   * @return {boolean}
   */
  isActivating () {
    return this._info.status === StatusStates.activating
  }

  /**
   * @return {boolean}
   */
  isActivated () {
    return this._info.status === StatusStates.activated
  }

  /**
   * @return {boolean}
   */
  isRedundant () {
    return this._info.status === StatusStates.redundant
  }

  /**
   * Delivers a push message to the supplied origin from the ServiceWorker
   * @param {string} data - The data of the message
   * @return {Promise<void>}
   * @see https://chromedevtools.github.io/devtools-protocol/tot/ServiceWorker#method-deliverPushMessage
   */
  async deliverPushMessage (data) {
    await this._manager.swDeliverPushMessage({
      registrationId: this._info.registrationId,
      origin: this._origin,
      data
    })
  }

  /**
   *
   * @param {string} tag
   * @param {boolean} [lastChance]
   * @return {Promise<void>}
   * @see https://chromedevtools.github.io/devtools-protocol/tot/ServiceWorker#method-dispatchSyncEvent
   */
  async dispatchSyncEvent (tag, lastChance) {
    await this._manager.swDispatchSyncEvent({
      origin: this._origin,
      registrationId: this._info.registrationId,
      tag,
      lastChance
    })
  }

  /**
   * Makes the ServiceWorker skip waiting
   * @return {Promise<void>}
   * @see https://chromedevtools.github.io/devtools-protocol/tot/ServiceWorker#method-skipWaiting
   */
  async skipWaiting () {
    await this._manager.makeServiceWorkerSkipWaiting(this._info.scopeURL)
  }

  /**
   * Starts the ServiceWorker
   * @return {Promise<void>}
   * @see https://chromedevtools.github.io/devtools-protocol/tot/ServiceWorker#method-startWorker
   */
  async start () {
    await this._manager.startServiceWorker(this._info.scopeURL)
  }

  /**
   * Stops the ServiceWorker
   * @param {string} versionId
   * @return {Promise<void>}
   * @see https://chromedevtools.github.io/devtools-protocol/tot/ServiceWorker#method-stopWorker
   */
  async stop (versionId) {
    await this._manager.stopServiceWorker(this._info.versionId)
  }

  /**
   * Un-registers the ServiceWorker
   * @return {Promise<void>}
   * @see https://chromedevtools.github.io/devtools-protocol/tot/ServiceWorker#method-unregister
   */
  async unregister () {
    await this._manager.unregisterServiceWorker(this._info.scopeURL)
  }

  /**
   * Updates the ServiceWorker's registrations
   * @return {Promise<void>}
   * @see https://chromedevtools.github.io/devtools-protocol/tot/ServiceWorker#method-updateRegistration
   */
  async updateRegistration () {
    await this._manager.updateServiceWorkerRegistration(this._info.scopeURL)
  }

  /**
   * @param {CDPServiceWorkerErrorMessage} error
   */
  _errorReported (error) {
    this.emit(Events.ServiceWorker.Error, error)
  }

  /**
   * @param {CDPServiceWorkerRegistration|CDPServiceWorkerVersion} updateHow
   * @param {boolean} isVersion
   */
  _bookKeeping (updateHow, isVersion) {
    // console.log(`ServiceWorker._bookKeeping: isVersion: ${isVersion}`, updateHow)
    let prevRegId = this._info.registrationId
    if (isVersion) {
      this._info = Object.assign(this._info, updateHow)
      this.emit(Events.ServiceWorker.VersionUpdated)
      if (this._info.isDeleted) {
        this._manager._removeServiceWorker(this.registrationId())
        this.emit(Events.ServiceWorker.Deleted)
      }
    } else {
      if (this._info.scopeURL !== updateHow.scopeURL) {
        const purl = new URL(updateHow.scopeURL)
        this._origin = purl.origin
      }
      this._info = Object.assign(this._info, updateHow)
      this.emit(Events.ServiceWorker.RegistrationUpdated)
    }
    if (prevRegId != null && prevRegId !== this._info.registrationId) {
      this._manager._ensureServiceWorkerSwapped(this, prevRegId)
    }
  }

  _destroyed () {
    this.emit(Events.ServiceWorker.Closed)
  }

  /**
   * @return {ServiceWorkerInfo}
   */
  toJSON () {
    return this._info
  }

  /** @ignore */
  // eslint-disable-next-line space-before-function-paren
  [util.inspect.custom](depth, options) {
    if (depth < 0) {
      return options.stylize(`[ServiceWorker]`, 'special')
    }
    const newOptions = Object.assign({}, options, {
      depth: options.depth == null ? null : options.depth - 1
    })
    const inner = util.inspect(this._info, newOptions)
    return `${options.stylize('ServiceWorker', 'special')} ${inner}`
  }
}

/**
 * @type {ServiceWorker}
 */
module.exports = ServiceWorker

const util = require('util')
const EventEmitter = require('eventemitter3')
const { debugError } = require('../helper')
const { ExecutionContext } = require('../executionContext')
const { JSHandle } = require('../JSHandle')

class Worker extends EventEmitter {
  /**
   * @param {Chrome|CRIConnection|CDPSession|Object} client
   * @param {string} url
   * @param {*} consoleAPICalled
   * @param {function(arg: Object): void} exceptionThrown
   */
  constructor (client, url, consoleAPICalled, exceptionThrown) {
    super()
    /**
     * @type {Chrome|CRIConnection|CDPSession|Object}
     * @private
     */
    this._client = client
    /**
     * @type {string}
     * @private
     */
    this._url = url
    /**
     * @type {Promise<ExecutionContext>}
     * @private
     */
    this._executionContextPromise = new Promise(
      resolve => (this._executionContextCallback = resolve)
    )
    /** @type {function(arg: Object):JSHandle} */
    let jsHandleFactory
    this._client.once('Runtime.executionContextCreated', async event => {
      jsHandleFactory = remoteObject =>
        new JSHandle(executionContext, client, remoteObject)
      const executionContext = new ExecutionContext(client, event.context, null)
      this._executionContextCallback(executionContext)
    })
    // This might fail if the target is closed before we receive all execution contexts.
    this._client.send('Runtime.enable', {}).catch(debugError)

    this._client.on('Runtime.consoleAPICalled', event =>
      consoleAPICalled(event, jsHandleFactory)
    )
    this._client.on('Runtime.exceptionThrown', exception =>
      exceptionThrown(exception.exceptionDetails)
    )
  }

  /**
   * @return {string}
   */
  url () {
    return this._url
  }

  /**
   * @return {Promise<ExecutionContext>}
   */
  executionContext () {
    return this._executionContextPromise
  }

  /**
   * @param {Function|string} pageFunction
   * @param {...*} args
   * @return {Promise<*>}
   */
  async evaluate (pageFunction, ...args) {
    const executionContext = await this._executionContextPromise
    return executionContext.evaluate(pageFunction, ...args)
  }

  /**
   * @param {Function|string} pageFunction
   * @param {...*} args
   * @return {Promise<JSHandle>}
   */
  async evaluateHandle (pageFunction, ...args) {
    const executionContext = await this._executionContextPromise
    return executionContext.evaluateHandle(pageFunction, ...args)
  }

  /**
   * @return {{url: string}}
   */
  toJSON () {
    return { url: this._url }
  }

  /** @ignore */
  // eslint-disable-next-line space-before-function-paren
  [util.inspect.custom](depth, options) {
    if (depth < 0) {
      return options.stylize('[Worker]', 'special')
    }

    const newOptions = Object.assign({}, options, {
      depth: options.depth == null ? null : options.depth - 1
    })
    const inner = util.inspect({ url: this._url }, newOptions)
    return `${options.stylize('Worker', 'special')} ${inner}`
  }
}

/**
 * @type {Worker}
 */
module.exports = Worker

const util = require('util')
const EventEmitter = require('eventemitter3')
const { CRIConnection } = require('../connection')
const ConsoleMessage = require('../ConsoleMessage')
const Events = require('../Events')
const { assert, helper, debugError } = require('../helper')
const ServiceWorker = require('./ServiceWorker')
const Worker = require('./Worker')

/**
 * Combination manger that juggles both Web Workers and ServiceWorkers
 * @see https://chromedevtools.github.io/devtools-protocol/tot/ServiceWorker
 * @since chrome-remote-interface-extra
 */
class WorkerManager extends EventEmitter {
  /**
   * @param {Chrome|CRIConnection|CDPSession|Object} client
   */
  constructor (client) {
    super()
    /** @type {Chrome|CRIConnection|CDPSession|Object} */
    this._client = client

    /** @type {Map<string, Worker>} */
    this._workers = new Map()

    /**
     * @type {Map<string, ServiceWorker>}
     */
    this._serviceWorkers = new Map()

    /**
     * @type {Map<string, string>}
     * @private
     */
    this._serviceWorkerTargets = new Map()

    /**
     * @type {boolean}
     * @private
     */
    this._serviceWorkersEnabled = false

    /**
     * @type {boolean}
     * @private
     */
    this._workersEnabled = false

    /**
     * @type {boolean}
     * @private
     */
    this._autoAttachEnabled = false

    this._client.on(
      'ServiceWorker.workerErrorReported',
      this._onWorkerErrorReported.bind(this)
    )

    this._client.on(
      'ServiceWorker.workerRegistrationUpdated',
      this._onWorkerRegistrationUpdated.bind(this)
    )

    this._client.on(
      'ServiceWorker.workerVersionUpdated',
      this._onWorkerVersionUpdated.bind(this)
    )

    this._client.on(
      'Target.attachedToTarget',
      this._onAttachedToTarget.bind(this)
    )

    this._client.on(
      'Target.detachedFromTarget',
      this._onDetachedFromTarget.bind(this)
    )

    this._addWorkerConsoleMessage = this._addWorkerConsoleMessage.bind(this)
    this._handleException = this._handleException.bind(this)
  }

  /**
   * Returns all ServiceWorkers the manager knows about
   * @return {Array<ServiceWorker>}
   */
  serviceWorkers () {
    return Array.from(this._serviceWorkers.values())
  }

  /**
   * Retrieve the ServiceWorker associated with the supplied registration id
   * @param {string} registrationId - The registration id of the desired ServiceWorker
   * @return {ServiceWorker}
   */
  serviceWorker (registrationId) {
    return this._serviceWorkers.get(registrationId)
  }

  /**
   * @return {Array<Worker>}
   */
  workers () {
    return Array.from(this._workers.values())
  }

  /**
   * @return {boolean}
   */
  serviceWorkerDomainEnabled () {
    return this._serviceWorkersEnabled
  }

  /**
   * @return {boolean}
   */
  workerMonitoringEnabled () {
    return this._workersEnabled
  }

  /**
   * Enables receiving service worker events
   * @return {Promise<void>}
   * @see https://chromedevtools.github.io/devtools-protocol/tot/ServiceWorker#method-enable
   */
  async enableServiceWorkerDomain () {
    if (this._serviceWorkersEnabled) return
    this._serviceWorkersEnabled = true
    await this._enableAutoAttach()
    await this._client.send('ServiceWorker.enable')
  }

  /**
   * Disables receiving service worker events
   * @return {Promise<void>}
   * @see https://chromedevtools.github.io/devtools-protocol/tot/ServiceWorker#method-disable
   */
  async disableServiceWorkerDomain () {
    if (!this._serviceWorkersEnabled) return
    this._serviceWorkersEnabled = false
    await this._disableAutoAttach()
    await this._client.send('ServiceWorker.disable')
  }

  /**
   * Enables the monitoring of Worker creation and removal
   * @return {Promise<void>}
   */
  async enableWorkerMonitoring () {
    if (this._workersEnabled) return
    this._workersEnabled = true
    await this._enableAutoAttach()
  }

  /**
   * Disables the monitoring of Worker creation and removal
   * @return {Promise<void>}
   */
  async disableWorkerMonitoring () {
    if (!this._workersEnabled) return
    this._workersEnabled = false
    await this._disableAutoAttach()
  }

  /**
   * @param {boolean} forceUpdateOnPageLoad
   * @return {Promise<void>}
   * @see https://chromedevtools.github.io/devtools-protocol/tot/ServiceWorker#method-setForceUpdateOnPageLoad
   */
  async swSetForceUpdateOnPageLoad (forceUpdateOnPageLoad) {
    if (!this._serviceWorkersEnabled) {
      throw new Error('Must enable service worker domain')
    }
    assert(
      helper.isBoolean(forceUpdateOnPageLoad),
      `The forceUpdateOnPageLoad param must be of type "string", received ${typeof forceUpdateOnPageLoad}`
    )
    this._forceUpdateState = forceUpdateOnPageLoad
    await this._client.send('ServiceWorker.setForceUpdateOnPageLoad', {
      forceUpdateOnPageLoad
    })
  }
  /**
   * Delivers a push message to the supplied origin from the ServiceWorker who's registrationId is the one supplied
   * @param {{origin: string, registrationId: string, data: string}} pushMessage
   * @return {Promise<void>}
   * @see https://chromedevtools.github.io/devtools-protocol/tot/ServiceWorker#method-deliverPushMessage
   */
  async swDeliverPushMessage ({ origin, registrationId, data }) {
    if (!this._serviceWorkersEnabled) {
      throw new Error('Must enable service worker domain')
    }
    assert(
      helper.isString(origin),
      `The origin must be of type "string", received ${typeof origin}`
    )
    assert(
      helper.isString(registrationId),
      `The registrationId must be of type "string", received ${typeof registrationId}`
    )
    assert(
      helper.isString(data),
      `The data sent must be of type "string", received ${typeof data}`
    )
    await this._client.send('ServiceWorker.deliverPushMessage', {
      origin,
      registrationId,
      data
    })
  }
  /**
   * Delivers a sync event to the supplied origin from the ServiceWorker who's registrationId is the one supplied
   * @param {{origin: string, registrationId: string, tag: string, lastChance: boolean}} syncEvent
   * @return {Promise<void>}
   * @see https://chromedevtools.github.io/devtools-protocol/tot/ServiceWorker#method-dispatchSyncEvent
   */
  async swDispatchSyncEvent ({ origin, registrationId, tag, lastChance }) {
    if (!this._serviceWorkersEnabled) {
      throw new Error('Must enable service worker domain')
    }
    assert(
      helper.isString(origin),
      `The origin must be of type "string", received ${typeof origin}`
    )
    assert(
      helper.isString(registrationId),
      `The registrationId must be of type "string", received ${typeof registrationId}`
    )
    assert(
      helper.isString(tag),
      `The data sent must be of type "string", received ${typeof data}`
    )
    await this._client.send('ServiceWorker.dispatchSyncEvent', {
      origin,
      registrationId,
      tag,
      lastChance: lastChance || false
    })
  }
  /**
   * Makes the ServiceWorker(s) under the supplied scope URL skip waiting
   * @param {string} scopeURL - The scope URL for the ServiceWorker(s)
   * @return {Promise<void>}
   * @see https://chromedevtools.github.io/devtools-protocol/tot/ServiceWorker#method-skipWaiting
   */
  async makeServiceWorkerSkipWaiting (scopeURL) {
    if (!this._serviceWorkersEnabled) {
      throw new Error('Must enable service worker domain')
    }
    assert(
      helper.isString(scopeURL),
      `The scopeURL param must be of type "string", received ${typeof scopeURL}`
    )
    await this._client.send('ServiceWorker.skipWaiting', { scopeURL })
  }

  /**
   * Starts the ServiceWorker(s) under the supplied scope URL
   * @param {string} scopeURL - The scope URL for the ServiceWorker(s) to be started
   * @return {Promise<void>}
   * @see https://chromedevtools.github.io/devtools-protocol/tot/ServiceWorker#method-startWorker
   */
  async startServiceWorker (scopeURL) {
    if (!this._serviceWorkersEnabled) {
      throw new Error('Must enable service worker domain')
    }
    assert(
      helper.isString(scopeURL),
      `The scopeURL param must be of type "string", received ${typeof scopeURL}`
    )
    await this._client.send('ServiceWorker.startWorker', { scopeURL })
  }

  /**
   * @return {Promise<void>}
   * @see https://chromedevtools.github.io/devtools-protocol/tot/ServiceWorker#method-stopAllWorkers
   */
  async stopAllServiceWorker () {
    await this._client.send('ServiceWorker.stopAllWorkers')
  }

  /**
   * @param {string} versionId
   * @return {Promise<void>}
   * @see https://chromedevtools.github.io/devtools-protocol/tot/ServiceWorker#method-stopWorker
   */
  async stopServiceWorker (versionId) {
    if (!this._serviceWorkersEnabled) {
      throw new Error('Must enable service worker domain')
    }
    assert(
      helper.isString(versionId),
      `The scopeURL param must be of type "string", received ${typeof versionId}`
    )
    await this._client.send('ServiceWorker.stopWorker', { versionId })
  }
  /**
   * Un-registers the ServiceWorker(s) registered under the supplied scope URL
   * @param {string} scopeURL - The scope URL to unregister the ServiceWorker(s) for
   * @return {Promise<void>}
   * @see https://chromedevtools.github.io/devtools-protocol/tot/ServiceWorker#method-unregister
   */
  async unregisterServiceWorker (scopeURL) {
    if (!this._serviceWorkersEnabled) {
      throw new Error('Must enable service worker domain')
    }
    assert(
      helper.isString(scopeURL),
      `The scopeURL param must be of type "string", received ${typeof scopeURL}`
    )
    await this._client.send('ServiceWorker.unregister', { scopeURL })
  }
  /**
   * Updates the ServiceWorker(s) registrations under the supplied scope URL
   * @param {string} scopeURL - The scope URL for the ServiceWorker(s) to update their registrations
   * @return {Promise<void>}
   * @see https://chromedevtools.github.io/devtools-protocol/tot/ServiceWorker#method-updateRegistration
   */
  async updateServiceWorkerRegistration (scopeURL) {
    assert(
      helper.isString(scopeURL),
      `The scopeURL param must be of type "string", received ${typeof scopeURL}`
    )
    await this._client.send('ServiceWorker.updateRegistration', { scopeURL })
  }

  /**
   * Clears the known service workers.
   * {@link Page} uses this method to get rid of them when navigating
   */
  _clearKnownWorkers () {
    for (const sw of this._serviceWorkers.values()) {
      sw._destroyed()
    }
    this._serviceWorkers.clear()
  }

  _removeServiceWorker (registrationId) {
    const sw = this._serviceWorkers.get(registrationId)
    if (sw) {
      this.emit(Events.WorkerManager.ServiceWorkerDeleted, sw)
    }
    this._serviceWorkers.delete(registrationId)
  }

  /**
   *
   * @param {ServiceWorker} swappedSW
   * @param {string} prevRegId
   */
  _ensureServiceWorkerSwapped (swappedSW, prevRegId) {
    const maybeSwappedOut = this._serviceWorkers.get(prevRegId)
    if (maybeSwappedOut && maybeSwappedOut !== swappedSW) {
      if (!this._serviceWorkers.has(swappedSW.registrationId())) {
        this._serviceWorkers.set(swappedSW.registrationId(), swappedSW)
      }
      this._removeServiceWorker(prevRegId)
    }
  }

  /**
   * @param {CDPServiceWorkerErrorMessage} errorMessage
   * @private
   */
  _onWorkerErrorReported ({ errorMessage }) {
    const sw = this._serviceWorkers.get(errorMessage.registrationId)
    if (sw) {
      sw._errorReported(errorMessage)
    }
  }

  /**
   * @param {{registrations: Array<CDPServiceWorkerRegistration>}} event
   * @private
   */
  _onWorkerRegistrationUpdated ({ registrations }) {
    for (let i = 0; i < registrations.length; i++) {
      this._updateSW(registrations[i])
    }
  }

  /**
   * @param {{versions: Array<CDPServiceWorkerVersion>}} event
   * @private
   */
  _onWorkerVersionUpdated ({ versions }) {
    for (let i = 0; i < versions.length; i++) {
      this._updateSW(versions[i], true)
    }
  }

  /**
   *
   * @param {CDPServiceWorkerRegistration|CDPServiceWorkerVersion} updateHow
   * @param {boolean} [isVersion = false]
   * @private
   */
  _updateSW (updateHow, isVersion = false) {
    // version or registration updates both have the registrationId property
    const regId = updateHow.registrationId
    let serviceWorker = this._serviceWorkers.get(regId)
    const isAdd = serviceWorker == null
    if (isAdd) {
      serviceWorker = new ServiceWorker(this)
      this._serviceWorkers.set(regId, serviceWorker)
    }
    serviceWorker._bookKeeping(updateHow, isVersion)
    if (isAdd) {
      this.emit(Events.WorkerManager.ServiceWorkerAdded, serviceWorker)
    }
  }

  _onAttachedToTarget (event) {
    // console.log(helper.inspect(event, { colors: true }))
    switch (event.targetInfo.type) {
      case 'worker':
        if (!this._workersEnabled) break
        const session = CRIConnection.fromSession(this._client).session(
          event.sessionId
        )
        const worker = new Worker(
          session,
          event.targetInfo.url,
          this._addWorkerConsoleMessage,
          this._handleException
        )
        this._workers.set(event.sessionId, worker)
        this.emit(Events.WorkerManager.WorkerCreated, worker)
        return
      case 'service_worker':
        // TODO evaluation
        // console.log(helper.inspect(event, { colors: true }))
        // this._serviceWorkerTargets.set(
        //   event.targetInfo.targetId,
        //   event.sessionId
        // )
        break
    }
    // service_worker type et al
    this._client
      .send('Target.detachFromTarget', {
        sessionId: event.sessionId
      })
      .catch(debugError)
  }

  /**
   * @param targetId
   * @return {boolean}
   */
  _isActiveServiceWorkerTarget (targetId) {
    return this._serviceWorkerTargets.has(targetId)
  }

  /**
   * @param {string} targetId
   * @return {?CDPSession}
   */
  _workerSession (targetId) {
    const sessionId = this._serviceWorkerTargets.get(targetId)
    if (!sessionId) return null
    return CRIConnection.fromSession(this._client).session(sessionId)
  }

  _onDetachedFromTarget (event) {
    const worker = this._workers.get(event.sessionId)
    if (!worker) return
    this._workers.delete(event.sessionId)
    this.emit(Events.WorkerManager.WorkerDestroyed, worker)
    // TODO sw evaluation
    // if (this._serviceWorkerTargets.has(event.targetId)) {
    //   this._serviceWorkerTargets.delete(event.targetId)
    //   console.log('detached from service worker target')
    //   let regID
    //   for (const sw of this._serviceWorkers.values()) {
    //     if (sw.targetId() === event.targetId) {
    //       regID = sw.registrationId()
    //       break
    //     }
    //   }
    //   if (regID) {
    //     this._removeServiceWorker(regID)
    //   }
    // }
  }

  /**
   * @param {Object} event
   * @param {function(arg: Object):JSHandle} jsHandleFactory
   */
  _addWorkerConsoleMessage (event, jsHandleFactory) {
    const message = new ConsoleMessage(event, { jsHandleFactory })
    this.emit(Events.WorkerManager.Console, message)
  }

  /**
   * @param {!Object} exceptionDetails
   */
  _handleException (exceptionDetails) {
    const message = helper.getExceptionMessage(exceptionDetails)
    const err = new Error(message)
    err.stack = '' // Don't report client-side error with a node stack attached
    this.emit(Events.WorkerManager.Error, err)
  }

  async _enableAutoAttach () {
    if (
      !this._autoAttachEnabled &&
      !this._workersEnabled &&
      !this._serviceWorkersEnabled
    ) {
      return
    }
    this._autoAttachEnabled = true
    await this._client.send('Target.setAutoAttach', {
      autoAttach: true,
      waitForDebuggerOnStart: false,
      flatten: true
    })
  }

  async _disableAutoAttach () {
    if (
      this._autoAttachEnabled &&
      ((this._workersEnabled && !this._serviceWorkersEnabled) ||
        (!this._workersEnabled && this._serviceWorkersEnabled))
    ) {
      return
    }
    this._autoAttachEnabled = false
    await this._client.send('Target.setAutoAttach', {
      autoAttach: false,
      waitForDebuggerOnStart: false,
      flatten: true
    })
  }

  /**
   * @return {{serviceWorkers: Array<ServiceWorker>}}
   */
  toJSON () {
    return { serviceWorkers: this.serviceWorkers(), workers: this.workers() }
  }

  /** @ignore */
  // eslint-disable-next-line space-before-function-paren
  [util.inspect.custom](depth, options) {
    if (depth < 0) {
      return options.stylize(`[ServiceWorkerManager]`, 'special')
    }
    const newOptions = Object.assign({}, options, {
      depth: options.depth == null ? null : options.depth - 1
    })
    const inner = util.inspect(
      {
        serviceWorkersDomainEnabled: this._serviceWorkersEnabled,
        workerMonitoringEnabled: this._workersEnabled,
        workers: this.workers(),
        serviceWorkers: this.serviceWorkers()
      },
      newOptions
    )
    return `${options.stylize('ServiceWorkerManager', 'special')} ${inner}`
  }
}

/**
 * @type {WorkerManager}
 */
module.exports = WorkerManager

/**
 * @type {ServiceWorker}
 */
exports.ServiceWorker = require('./ServiceWorker')

/**
 * @type {Worker}
 */
exports.Worker = require('./Worker')

/**
 * @type {WorkerManager}
 */
exports.WorkerManager = require('./WorkerManager')
